import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { items: [], text: '' };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <div>
            <DragableTextList items={this.state.items}/>
            <form onSubmit={this.handleSubmit}>
              <input 
              id="input_dragable_text"
              onChange={this.handleChange}
              value={this.state.text}
              />
              <button>
                input
              </button>
            </form>
          </div>
        </header>
      </div>
    );
  }

  handleChange(e) {
    this.setState({ text: e.target.value });
  }

  handleSubmit(e) {
    e.preventDefault();
    if (this.state.text.length === 0) {
      return;
    }
    const newItem = {
      text: this.state.text,
      id: Date.now()
    };
    this.setState(state => ({
      items: state.items.concat(newItem),
      text: ''
    }));
  }
}


class DragableTextList extends React.Component {
  constructor(props) {
    super(props);
    this.items = props.items
  }

  render() {
    const elements = [
      ["asf", 1],
      ["bbb", 2]
    ];

    for(const item of this.items.entries()) {
      elements.push(<DragableDiv value={item}></DragableDiv>)
    }
    return (
      <div>
        {elements}
      </div>
    )
  }
}


function DragableDiv(item) {
  return (
    <div class="dragable_div" id={item.id}>
      <div class="mydivheader" id={item.id}>{item.value}</div>
    </div>
  )
}

export default App;
