import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { text: '', items: [] };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <div>
            <DragableTextList items={this.state.items}/>
            <form onSubmit={this.handleSubmit}>
              <input 
              id="input_dragable_text"
              onChange={this.handleChange}
              value={this.state.text}
              />
              <button>
                input
              </button>
            </form>
          </div>
        </header>
      </div>
    );
  }

  handleChange(e) {
    this.setState({ text: e.target.value });
  }

  handleSubmit(e) {
    e.preventDefault();
    if (this.state.text.length === 0) {
      return;
    }
    const newItem = {
      text: this.state.text
    };
    this.setState(state => ({
      items: state.items.concat(newItem),
      text: ''
    }));
  }
}


class DragableTextList extends React.Component {
  constructor(props) {
    super(props);
    this.items = props.items
    var dragElements = document.getElementsByClassName("dragable_div");
    for (var i=0; i<dragElements.length; i++) {
      dragElement(dragElements[i]);
    }
  }

  render() {
    const elements = [];
    const text_array = this.props.items //[{text: "a"}]
    //console.log()
    for(const item of text_array) {
      elements.push(<DragableDiv value={item["text"]}></DragableDiv>)
    }

    return (
      <div>
        {elements}
      </div>
    )
  }
}


function DragableDiv(content) {
  const div_id =Date.now()
  return (
    <div class="dragable_div" id={div_id}>
      <div class="mydivheader" id={div_id}>{content.value}</div>
    </div>
  )

  function dragElement(elmnt) {
    var pos1 = 0, pos2 = 0, pos3 = 0, pos4 = 0;
    if (document.getElementById(elmnt.id + "header")) {
      /* if present, the header is where you move the DIV from:*/
      document.getElementById(elmnt.id + "header").onmousedown = dragMouseDown;
    } else {
      /* otherwise, move the DIV from anywhere inside the DIV:*/
      elmnt.onmousedown = dragMouseDown;
    }

    function dragMouseDown(e) {
      e = e || window.event;
      e.preventDefault();
      // get the mouse cursor position at startup:
      pos3 = e.clientX;
      pos4 = e.clientY;
      document.onmouseup = closeDragElement;
      // call a function whenever the cursor moves:
      document.onmousemove = elementDrag;
    }

    function elementDrag(e) {
      e = e || window.event;
      e.preventDefault();
      // calculate the new cursor position:
      pos1 = pos3 - e.clientX;
      pos2 = pos4 - e.clientY;
      pos3 = e.clientX;
      pos4 = e.clientY;
      // set the element's new position:
      elmnt.style.top = (elmnt.offsetTop - pos2) + "px";
      elmnt.style.left = (elmnt.offsetLeft - pos1) + "px";
    }

    function closeDragElement() {
      /* stop moving when mouse button is released:*/
      document.onmouseup = null;
      document.onmousemove = null;
    }
  }
}

export default App;
