import React, {useState} from 'react';
import Draggable from "./Draggable"
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { text: '', items: [] };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <div>
            <form onSubmit={this.handleSubmit}>
              <input 
              id="input_dragable_text"
              onChange={this.handleChange}
              value={this.state.text}
              placeholder = "Press Enter to input"
              />
            </form>
          </div>
        </header>
        <div className="App-body">
          <DragableTextList items={this.state.items}/>
        </div>
      </div>
    );
  }

  handleChange(e) {
    this.setState({ text: e.target.value });
  }

  handleSubmit(e) {
    e.preventDefault();
    if (this.state.text.length === 0) {
      return;
    }
    const newItem = {
      text: this.state.text,
      id: new Date().toLocaleString(),
    };

    this.setState(state => ({
      items: state.items.concat(newItem),
      text: ''
    }));
  }
}


class DragableTextList extends React.Component {
  constructor(props) {
    super(props);
    this.items = props.items;
  }

  render() {
    const elements = [];
    //var items_array = useState(this.props.items);
    //console.log()

    //remove the Draggable class
    function handleRemove(id) {
        console.log(id);
        //const new_items_array = items_array.filter((item) => item.id !== id);
        //items_array = new_items_array;
        //elements.splice(1, 1);
    }

    for(const item of this.items) {
        elements.push(
            <Draggable>
                <button onClick={() => handleRemove(item.id)}>remove</button>
                <div id={item.id}>{item.text}</div>
            </Draggable>
        )
    }

    return (
      <div>
        {elements}
      </div>
    )
  }
}

export default App;
