import React, {useState} from 'react';
import Draggable from "./Draggable"
import './App.css';

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = { text: '', items: [], elements: [] };
        this.elements = [];
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleClick = this.handleRemove.bind(this);
    }

    handleRemove(id) {
        console.log(id);
        const new_items = this.state.items.filter((item) => item.id !== id);
        const new_elements = this.elements.filter((item) => item.id !== id);
        this.setElements
    }

  render() {
    //const elements = [];

    //remove the Draggable class

    this.elements = [];
    for(const item of this.state.items) {
        this.elements.push(
            <Draggable id={item.id}>
                <button onClick={() => this.handleRemove(item.id)}>remove</button>
                <div >{item.text}</div>
            </Draggable>
        )
    }

    return (
        <div className="App">
            <header className="App-header">
            <div>
                <form onSubmit={this.handleSubmit}>
                <input 
                id="input_dragable_text"
                onChange={this.handleChange}
                value={this.state.text}
                placeholder = "Press Enter to input"
                />
                </form>
            </div>
            </header>
            <div className="App-body">
            {this.elements}
            </div>
        </div>
    );
  }

  handleChange(e) {
    this.setState({ text: e.target.value });
  }

  handleSubmit(e) {
        e.preventDefault();
        if (this.state.text.length === 0) {
            return;
        }
        const newItem = {
            text: this.state.text,
            id: new Date().toLocaleString(),
        };
        this.setState(state => ({
            items: state.items.concat(newItem),
            text: ''
        }));
    }
}

export default App;
