import React from 'react';
import './App.css';

const throttle = (f) => {
  let token = null, lastArgs = null;
  const invoke = () => {
      f(...lastArgs);
      token = null;
  };
  const result = (...args) => {
      lastArgs = args;
      if (!token) {
          token = requestAnimationFrame(invoke);
      }
  };
  result.cancel = () => token && cancelAnimationFrame(token);
  return result;
};

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { text: '', items: [] };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <div>
            <DragableTextList items={this.state.items}/>
            <form onSubmit={this.handleSubmit}>
              <input 
              id="input_dragable_text"
              onChange={this.handleChange}
              value={this.state.text}
              />
              <button>
                input
              </button>
            </form>
          </div>
        </header>
      </div>
    );
  }

  handleChange(e) {
    this.setState({ text: e.target.value });
  }

  handleSubmit(e) {
    e.preventDefault();
    if (this.state.text.length === 0) {
      return;
    }
    const newItem = {
      text: this.state.text
    };
    this.setState(state => ({
      items: state.items.concat(newItem),
      text: ''
    }));
  }
}


class DragableTextList extends React.Component {
  constructor(props) {
    super(props);
    this.items = props.items
  }

  render() {
    const elements = [];
    const text_array = [{text: "a"}] //this.props.items
    //console.log()
    for(const item of text_array) {
      elements.push(<Test />)
    }
    return (
      <div>
        {elements}
      </div>
    )
  }
}

class Draggable extends React.PureComponent {
  _relX = 0;
  _relY = 0;
  _ref = React.createRef();

  _onMouseDown = (event) => {
      if (event.button !== 0) {
          return;
      }
      const {scrollLeft, scrollTop, clientLeft, clientTop} = document.body;
      // Try to avoid calling `getBoundingClientRect` if you know the size
      // of the moving element from the beginning. It forces reflow and is
      // the laggiest part of the code right now. Luckily it's called only
      // once per click.
      const {left, top} = this._ref.current.getBoundingClientRect();
      this._relX = event.pageX - (left + scrollLeft - clientLeft);
      this._relY = event.pageY - (top + scrollTop - clientTop);
      document.addEventListener('mousemove', this._onMouseMove);
      document.addEventListener('mouseup', this._onMouseUp);
      event.preventDefault();
  };

  _onMouseUp = (event) => {
      document.removeEventListener('mousemove', this._onMouseMove);
      document.removeEventListener('mouseup', this._onMouseUp);
      event.preventDefault();
  };

  _onMouseMove = (event) => {
      this.props.onMove(
          event.pageX - this._relX,
          event.pageY - this._relY,
      );
      event.preventDefault();
  };

  _update = throttle(() => {
      const {x, y} = this.props;
      this._ref.current.style.transform = `translate(${x}px, ${y}px)`;
  });

  componentDidMount() {
      this._ref.current.addEventListener('mousedown', this._onMouseDown);
      this._update();
  }

  componentDidUpdate() {
      this._update();
  }

  componentWillUnmount() {
      this._ref.current.removeEventListener('mousedown', this._onMouseDown);
      this._update.cancel();
  }

  render() {
      return (
          <div className="draggable" ref={this._ref}>
              {this.props.children}
          </div>
      );
  }
}

class Test extends React.PureComponent {
  state = {
      x: 100,
      y: 200,
  };

  _move = (x, y) => this.setState({x, y});

  // you can implement grid snapping logic or whatever here
  /*
  _move = (x, y) => this.setState({
      x: ~~((x - 5) / 10) * 10 + 5,
      y: ~~((y - 5) / 10) * 10 + 5,
  });
  */

  render() {
      const {x, y} = this.state;
      return (
          <Draggable x={x} y={y} onMove={this._move}>
              Drag me
          </Draggable>
      );
  }
}



