import React, {useState} from 'react';
import Draggable from "./Draggable"
import './App.css';

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = { text: '', items: [] };
        this.elements = [];
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleClick = this.handleRemove.bind(this);
    }

    handleRemove(key) {
        console.log(key);
        console.log(this.state.items);
        const new_items = this.state.items.filter((item) => item.key !== key);
        this.setState({text: this.state.text, items: new_items})
    }

  render() {
    //const elements = [];

    //remove the Draggable class
    /*
    this.elements = [];
    for(const item of this.state.items) {
        this.elements.push(
            <Draggable id={item.id}>
                <button onClick={() => this.handleRemove(item.id)}>remove</button>
                <div >{item.text}</div>
            </Draggable>
        )
    }
    */

    return (
        <div className="App">
            <header className="App-header">
            <div>
                <form onSubmit={this.handleSubmit}>
                <input 
                id="input_dragable_text"
                onChange={this.handleChange}
                value={this.state.text}
                placeholder = "Press Enter to input"
                />
                </form>
            </div>
            </header>
            <div className="App-body">
            {this.state.items}
            </div>
        </div>
    );
  }

  handleChange(e) {
    this.setState({ text: e.target.value });
  }

  handleSubmit(e) {
        e.preventDefault();
        if (this.state.text.length === 0) {
            return;
        }
        const id =new Date().toLocaleString();
        const newItem = 
        //must have unique key to identify the Draggable class
        <Draggable key={id}>
            <div onClick={() => this.handleRemove(id)} class="remove_button"style={{backgroundColor: "powderblue"}}>remove</div> 
            <div>{this.state.text}</div>
        </Draggable>;
        /*
        {
            text: this.state.text,
            id: new Date().toLocaleString(),
        };
        */
        this.setState(state => ({
            items: state.items.concat(newItem),
            text: ''
        }));
    }
}

export default App;
